Method.Implementation:
set retVal=""
try {f
	set obj=$classmethod(ClassName,"%OpenId",id)
	if $get(obj)="" quit
	set arrayObj=$property(obj,arrayProp)
	for i=1:1:arrayObj.Count() {
		set val=arrayObj.GetNext(.elementKey)
		if elementKey="" quit
		set $LIST(retVal,i)=elementKey
	}
}
catch ex {
	//write ex.Name,"-",ex.Location,!
}
quit retVal

set retVal=0
try {
	set obj=$classmethod(ClassName,"%OpenId",id)
	set arrayObj=$property(obj,arrayProp)
	for i=1:1:arrayObj.Count() {
		set serialObj=arrayObj.GetNext(.elementKey)
		if elementKey="" quit
		if elementKey=Flg {
			set retVal=$property(serialObj,PropName)
		}
	}
}
catch ex {
	//write ex.Name,"-",ex.Location,!
}
quit retVal
